mapper.select:
select *
from admin
where flag = #{flag} and id = #{id} and pwd = #{pwd}

select distinct collage
from student
where flag = #{flag}

select distinct grade
from student
where flag = #{flag}

select distinct major
from student
where flag = #{flag}

select distinct "studentClass"
from student
where flag = #{flag}

select distinct address
from company
where flag = #{flag}

mapper.update:
update student
set flag = #{flag}
where id = #{id}

update company
set flag = #{flag}
where id = #{id}

mapper.select:
select *
from student
where 1=1
    and flag = #{flag}

    and grade = #{grade}


    and collage = #{collage}


    and major = #{major}


    and "studentClass" = #{stuClass}

select *
from company
where 1=1
    and flag = #{flag}

    and id = #{id}


    and company."createTime" >= #{start}


    and #{end} >= company."createTime"


    and address = #{address}

mapper.update:
update student
set pwd = #{pwd}
where id = #{id}

update company
set pwd = #{pwd}
where id = #{id}

mapper.select:
