mapper.update:
update notice
set "publishTime" = #{publishTime}, collage = #{collage},
    title = #{notice.title}, content = #{notice.content},"documentId" = #{notice.documentId}
where id = #{notice.id}

update notice
set flag = #{flag}
where id = #{id}

mapper.insert:
insert into requirement (gender, major, content, degree, "minYear", "maxYear")
values (#{requirement.gender}, #{requirement.major}, #{requirement.content}, #{requirement.degree},
        #{requirement.minYear}, #{requirement.maxYear});

mapper.select:
