mapper.select:
select *
from company
where 1=1
    and flag = #{flag}

    and id = #{id}


    and company."createTime" >= #{start}


    and #{end} >= company."createTime"


    and address = #{address}

select *
from document
where flag = #{flag}

    and id = #{id}


    and name like #{name}

select distinct count(*)
from document
where flag = #{flag}

    and name like #{name}

mapper.update:
update document
set flag = #{flag}
where id = #{id}

mapper.select:
select *
from notice

    where id = #{id}


    where flag = #{flag}


    where flag = #{flag} and id = #{id}

select *
from notice
where 1 = 1

    and type = #{type}


    and name like #{name}


    and time between #{timeBefore} and #{timeLast}

mapper.update:
update notice
set "publishTime" = #{publishTime}, collage = #{collage},
    title = #{notice.title}, content = #{notice.content},"documentId" = #{notice.documentId}
where id = #{notice.id}

update notice
set flag = #{flag}
where id = #{id}

mapper.select:
select *
from recruit
where 1 = 1

    and id = #{id}


    and flag = #{flag}


    and companyScc = #{companyScc}


    and major like %#{major}%

mapper.update:
update recruit
set flag = #{flag}
where id = #{id}

mapper.select:
select *
from resume
where flag = #{flag}

    and id = #{id}

mapper.update:
update resume
set flag = #{flag}
where id = #{id}

mapper.select:
select *
from employMeeting

    where flag = #{flag} and id = #{id}


    where flag = #{flag}


    where id = #{id}

mapper.update:
update meeting
set flag = #{flag}
where id = #{id}

mapper.select:
select advice
from questionaire

    where grade = #{grade}

select count(*)
from employment
where studentId in (
    select id from student where 1 = 1

        and grade = #{grade}


        and collage = #{collage}


        and major = #{major}

)
  and employmentStatus = #{employmentStatus}
  and workProperty = #{workProperty}

select companyScc, count(*)
from experience
where employmentId in (
    select id from employment where studentId in (
        select id from student where 1 = 1

            and grade = #{grade}


            and collage = #{collage}


            and major = #{major}

    )
)
and workProperty = #{workProperty}
group by companyScc

select workPlace, count(*)
from experience
where employmentId in (
    select id from employment where studentId in (
        select id from student where 1 = 1

            and grade = #{grade}


            and collage = #{collage}


            and major = #{major}

    )
)
and workProperty = #{workProperty}
group by workPlace
